mapper.select:
select
	*
from
	employee


			and emp_id = #{empId}


			and last_name like CONCAT('%', #{lastName}, '%')


			and fast_name like CONCAT('%', #{fastName}, '%')


			and last_name_kana like CONCAT('%', #{lastNameKana}, '%')


			and fast_name_kana like CONCAT('%', #{fastNameKana}, '%')


			and gender = #{gender}


			and birthday = #{birthday}


			and blood_type = #{bloodType}


	order by emp_id

update employee
set
	 EMP_ID = #{empId}
	,LAST_NAME = #{lastName}
	,FAST_NAME = #{fastName}
	,LAST_NAME_KANA = #{lastNameKana}
	,FAST_NAME_KANA = #{fastNameKana}
	,GENDER = #{gender}
	,BIRTHDAY = #{birthday}
	,BLOOD_TYPE = #{bloodType}
where emp_id = #{empId}
